// ============================================
// AUTOMATED SOLANA CHECKHC PAYMENT
// ============================================

const { Connection, Keypair, PublicKey, Transaction, SystemProgram } = require('@solana/web3.js');
const { getAssociatedTokenAddress, createTransferInstruction } = require('@solana/spl-token');
const bs58 = require('bs58');

// Get data from previous nodes
const pricingData = $('1. Get Pricing').first().json;
const uploadData = $('2. Upload Document').first().json;
const inputData = $('Input Data').first().json;

// Configuration
const RPC_URL = 'https://api.mainnet-beta.solana.com'; // Use your RPC
const CHECKHC_MINT = new PublicKey(pricingData.checkhc_mint);
const PAYMENT_WALLET = new PublicKey(pricingData.payment_wallet);
const AMOUNT_CHECKHC = pricingData.price_checkhc;

// Get private key from credentials (stored as base58 string)
const credentials = await this.getCredentials('solanaWallet');
const privateKeyBase58 = credentials.privateKey;
const payerKeypair = Keypair.fromSecretKey(bs58.decode(privateKeyBase58));

console.log('Payer wallet:', payerKeypair.publicKey.toString());
console.log('Payment amount:', AMOUNT_CHECKHC, 'CHECKHC');
console.log('Payment to:', PAYMENT_WALLET.toString());

// Connect to Solana
const connection = new Connection(RPC_URL, 'confirmed');

// Get associated token accounts
const payerTokenAccount = await getAssociatedTokenAddress(
  CHECKHC_MINT,
  payerKeypair.publicKey
);

const recipientTokenAccount = await getAssociatedTokenAddress(
  CHECKHC_MINT,
  PAYMENT_WALLET
);

console.log('Payer token account:', payerTokenAccount.toString());
console.log('Recipient token account:', recipientTokenAccount.toString());

// Check balance
const balance = await connection.getTokenAccountBalance(payerTokenAccount);
console.log('Current balance:', balance.value.uiAmount, 'CHECKHC');

if (balance.value.uiAmount < AMOUNT_CHECKHC) {
  throw new Error(`Insufficient CHECKHC balance. Need ${AMOUNT_CHECKHC}, have ${balance.value.uiAmount}`);
}

// Convert CHECKHC to raw amount (CHECKHC has 6 decimals)
const amountRaw = Math.floor(AMOUNT_CHECKHC * 1000000);

console.log('Amount raw (with decimals):', amountRaw);

// Create transfer instruction
const transferInstruction = createTransferInstruction(
  payerTokenAccount,
  recipientTokenAccount,
  payerKeypair.publicKey,
  amountRaw
);

// Get recent blockhash
const { blockhash, lastValidBlockHeight } = await connection.getLatestBlockhash('confirmed');

// Create and sign transaction
const transaction = new Transaction();
transaction.recentBlockhash = blockhash;
transaction.feePayer = payerKeypair.publicKey;
transaction.add(transferInstruction);
transaction.sign(payerKeypair);

console.log('Transaction created and signed');

// Send transaction
const signature = await connection.sendRawTransaction(
  transaction.serialize(),
  { skipPreflight: false, preflightCommitment: 'confirmed' }
);

console.log('Transaction sent:', signature);
console.log('Waiting for confirmation...');

// Wait for confirmation
const confirmation = await connection.confirmTransaction(
  {
    signature,
    blockhash,
    lastValidBlockHeight
  },
  'confirmed'
);

if (confirmation.value.err) {
  throw new Error(`Transaction failed: ${JSON.stringify(confirmation.value.err)}`);
}

console.log('Payment confirmed!');

// Return payment signature and all necessary data
return {
  payment_signature: signature,
  payment_amount: AMOUNT_CHECKHC,
  payment_wallet: PAYMENT_WALLET.toString(),
  payer_wallet: payerKeypair.publicKey.toString(),
  storage_id: uploadData.storage_id,
  checkhc_mint: pricingData.checkhc_mint,
  confirmed: true,
  confirmation_url: `https://solscan.io/tx/${signature}`
};

