// ==================================================================
// STEP 3: AUTOMATIC SWAP SOL ‚Üí CHECKHC (if needed) + PAYMENT
// ==================================================================
// This step replicates the web flow: check balance, swap if needed, then pay

const { Connection, Keypair, PublicKey, VersionedTransaction } = require('@solana/web3.js');
const { getAssociatedTokenAddress, createTransferInstruction, getAccount } = require('@solana/spl-token');
const bs58 = require('bs58');
const fetch = require('node-fetch');

// Get data from previous nodes
const pricingData = $('1. Get Pricing').first().json;
const uploadData = $('2. Upload Document').first().json;
const inputData = $('Input Data').first().json;

// Configuration
const RPC_URL = 'https://api.mainnet-beta.solana.com';
const JUPITER_API = 'https://quote-api.jup.ag/v6';
const SOL_MINT = 'So11111111111111111111111111111111111111112';
const CHECKHC_MINT = pricingData.checkhc_mint;
const PAYMENT_WALLET = new PublicKey(pricingData.payment_wallet);
const AMOUNT_CHECKHC_NEEDED = pricingData.price_checkhc;
const SLIPPAGE_BPS = 100; // 1%

// Get wallet credentials
const credentials = await this.getCredentials('solanaWallet');
const privateKeyBase58 = credentials.privateKey;
const payerKeypair = Keypair.fromSecretKey(bs58.decode(privateKeyBase58));

console.log('\nüöÄ AUTOMATED SWAP + PAYMENT WORKFLOW');
console.log('Payer wallet:', payerKeypair.publicKey.toString());
console.log('Amount needed:', AMOUNT_CHECKHC_NEEDED, 'CHECKHC');

// Connect to Solana
const connection = new Connection(RPC_URL, 'confirmed');

// ============================================
// PHASE 1: CHECK CHECKHC BALANCE
// ============================================
const checkhcMint = new PublicKey(CHECKHC_MINT);
const checkhcTokenAccount = await getAssociatedTokenAddress(
  checkhcMint,
  payerKeypair.publicKey
);

let currentCHECKHCBalance = 0;
try {
  const accountInfo = await getAccount(connection, checkhcTokenAccount);
  currentCHECKHCBalance = Number(accountInfo.amount) / 1000000; // 6 decimals
  console.log('\nüí∞ Current CHECKHC balance:', currentCHECKHCBalance);
} catch (error) {
  console.log('\n‚ö†Ô∏è No CHECKHC token account yet (will be created during swap)');
}

const checkhcDeficit = Math.max(0, AMOUNT_CHECKHC_NEEDED - currentCHECKHCBalance);

// ============================================
// PHASE 2: SWAP SOL ‚Üí CHECKHC (if needed)
// ============================================
let swapSignature = null;
let solSpent = 0;
let checkhcReceived = 0;

if (checkhcDeficit > 0) {
  console.log('\nüîÑ Need to swap', checkhcDeficit, 'more CHECKHC');

  const outputAmountRaw = Math.ceil(checkhcDeficit * 1000000);

  // Get Jupiter quote
  const quoteUrl = `${JUPITER_API}/quote?inputMint=${SOL_MINT}&outputMint=${CHECKHC_MINT}&amount=${outputAmountRaw}&slippageBps=${SLIPPAGE_BPS}&swapMode=ExactOut`;

  const quoteResponse = await fetch(quoteUrl);
  if (!quoteResponse.ok) {
    throw new Error(`Jupiter quote failed: ${quoteResponse.statusText}`);
  }

  const quoteData = await quoteResponse.json();
  const solNeeded = Number(quoteData.inAmount) / 1000000000; // 9 decimals

  console.log('üìä Jupiter Quote:');
  console.log('  - SOL needed:', solNeeded);
  console.log('  - CHECKHC to receive:', Number(quoteData.outAmount) / 1000000);
  console.log('  - Price impact:', quoteData.priceImpactPct, '%');

  // Check SOL balance
  const solBalance = await connection.getBalance(payerKeypair.publicKey);
  const solBalanceFormatted = solBalance / 1000000000;

  if (solBalanceFormatted < solNeeded + 0.01) {
    throw new Error(`Insufficient SOL. Need ${solNeeded + 0.01} SOL, have ${solBalanceFormatted} SOL`);
  }

  // Get swap transaction
  const swapResponse = await fetch(`${JUPITER_API}/swap`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({
      quoteResponse: quoteData,
      userPublicKey: payerKeypair.publicKey.toString(),
      wrapAndUnwrapSol: true,
      dynamicComputeUnitLimit: true,
      prioritizationFeeLamports: 'auto'
    })
  });

  if (!swapResponse.ok) {
    throw new Error(`Swap transaction failed: ${swapResponse.statusText}`);
  }

  const swapData = await swapResponse.json();
  const swapTransactionBuf = Buffer.from(swapData.swapTransaction, 'base64');
  let transaction = VersionedTransaction.deserialize(swapTransactionBuf);

  transaction.sign([payerKeypair]);

  console.log('üì§ Sending swap transaction...');
  swapSignature = await connection.sendRawTransaction(transaction.serialize(), {
    skipPreflight: false,
    maxRetries: 3
  });

  console.log('Swap TX:', swapSignature);

  // Wait for confirmation
  const latestBlockhash = await connection.getLatestBlockhash('confirmed');
  const confirmation = await connection.confirmTransaction({
    signature: swapSignature,
    blockhash: latestBlockhash.blockhash,
    lastValidBlockHeight: latestBlockhash.lastValidBlockHeight
  }, 'confirmed');

  if (confirmation.value.err) {
    throw new Error(`Swap failed: ${JSON.stringify(confirmation.value.err)}`);
  }

  console.log('‚úÖ Swap successful!');

  // Verify new balance
  const newAccountInfo = await getAccount(connection, checkhcTokenAccount);
  const newBalance = Number(newAccountInfo.amount) / 1000000;

  solSpent = solNeeded;
  checkhcReceived = newBalance - currentCHECKHCBalance;
  currentCHECKHCBalance = newBalance;

  console.log('New CHECKHC balance:', currentCHECKHCBalance);
} else {
  console.log('\n‚úÖ Sufficient CHECKHC balance - no swap needed');
}

// ============================================
// PHASE 3: PAY CERTIFICATION
// ============================================
console.log('\nüí≥ Paying certification...');

const paymentTokenAccount = await getAssociatedTokenAddress(
  checkhcMint,
  PAYMENT_WALLET
);

const amountRaw = Math.floor(AMOUNT_CHECKHC_NEEDED * 1000000);

const transferIx = createTransferInstruction(
  checkhcTokenAccount,
  paymentTokenAccount,
  payerKeypair.publicKey,
  amountRaw
);

const { blockhash, lastValidBlockHeight } = await connection.getLatestBlockhash('confirmed');

const { Transaction } = require('@solana/web3.js');
const paymentTx = new Transaction();
paymentTx.recentBlockhash = blockhash;
paymentTx.feePayer = payerKeypair.publicKey;
paymentTx.add(transferIx);
paymentTx.sign(payerKeypair);

const paymentSignature = await connection.sendRawTransaction(
  paymentTx.serialize(),
  { skipPreflight: false, preflightCommitment: 'confirmed' }
);

console.log('Payment TX:', paymentSignature);

const paymentConfirmation = await connection.confirmTransaction({
  signature: paymentSignature,
  blockhash,
  lastValidBlockHeight
}, 'confirmed');

if (paymentConfirmation.value.err) {
  throw new Error(`Payment failed: ${JSON.stringify(paymentConfirmation.value.err)}`);
}

console.log('‚úÖ Payment confirmed!\n');

// Return complete transaction data
return {
  // Swap data
  swap_executed: checkhcDeficit > 0,
  swap_signature: swapSignature,
  sol_spent: solSpent,
  checkhc_received: checkhcReceived,
  checkhc_balance_before: currentCHECKHCBalance - checkhcReceived,
  checkhc_balance_after: currentCHECKHCBalance,

  // Payment data
  payment_signature: paymentSignature,
  payment_amount: AMOUNT_CHECKHC_NEEDED,
  payment_wallet: PAYMENT_WALLET.toString(),
  payer_wallet: payerKeypair.publicKey.toString(),

  // Links
  swap_url: swapSignature ? `https://solscan.io/tx/${swapSignature}` : null,
  payment_url: `https://solscan.io/tx/${paymentSignature}`,

  // Storage data for next step
  storage_id: uploadData.storage_id,
  confirmed: true
};

